import React from 'react';

export default function EventAnnouncement({ eventName = 'Community Meetup', date = '2025-10-10', time = '6:00 PM', location = 'Main Hall', rsvpUrl = 'https://example.com/rsvp' }) {
  return (
    <html>
      <head>
        <meta charSet="utf-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>{eventName} — You"re Invited!</title>
        <style>{`
          body { font-family: Arial, sans-serif; color: #333; margin: 0; padding: 0; }
          .hero { background: #0ea5e9; color: white; padding: 24px; text-align: center; }
          .container { max-width: 640px; margin: 0 auto; padding: 24px; }
          .card { background: #ffffff; border: 1px solid #e5e7eb; border-radius: 8px; padding: 24px; }
          .row { margin: 8px 0; }
          .label { color: #6b7280; font-size: 12px; text-transform: uppercase; letter-spacing: 0.08em; }
          .value { font-size: 16px; font-weight: 600; }
          .button { display: inline-block; background: #0ea5e9; color: #ffffff; padding: 12px 20px; border-radius: 6px; text-decoration: none; margin-top: 16px; }
          .footer { text-align: center; color: #6b7280; font-size: 12px; margin-top: 24px; }
        `}</style>
      </head>
      <body>
        <div className="hero">
          <h1 style={{ margin: 0 }}>{eventName}</h1>
          <p style={{ margin: '8px 0 0' }}>You"re invited!</p>
        </div>
        <div className="container">
          <div className="card">
            <div className="row">
              <div className="label">Date</div>
              <div className="value">{date}</div>
            </div>
            <div className="row">
              <div className="label">Time</div>
              <div className="value">{time}</div>
            </div>
            <div className="row">
              <div className="label">Location</div>
              <div className="value">{location}</div>
            </div>
            <a href="{rsvpUrl}" className="button">RSVP Now</a>
          </div>
          <div className="footer">
            © {new Date().getFullYear()} Event Team
          </div>
        </div>
      </body>
    </html>
  );
}
