{"name":"welcome-series","description":"Welcome email series for new subscribers","category":"newsletters","jsxCode":"import React from 'react';

interface WelcomeEmailProps {
  firstName: string;
  lastName: string;
}

const WelcomeEmail: React.FC<WelcomeEmailProps> = ({ firstName, lastName }) => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '600px', margin: '0 auto' }}>
      <h1 style={{ color: '#333', textAlign: 'center' }}>Welcome to Our Newsletter!</h1>
      <p>Dear {firstName} {lastName},</p>
      <p>Thank you for subscribing to our newsletter. We're excited to have you on board!</p>
      <p>You'll receive our latest updates, exclusive content, and special offers.</p>
      <p>Best regards,<br/>The Newsletter Team</p>
    </div>
  );
};

export default WelcomeEmail;"}
