import React from 'react';

export default function SimpleNewsletter({ userName, weekNumber, featuredStory }) {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ color: '#333', textAlign: 'center' }}>Weekly Newsletter</h1>
      <p>Hello {userName},</p>
      <p>Welcome to Week {weekNumber} of our newsletter!</p>

      <div style={{ backgroundColor: '#f4f4f4', padding: '20px', margin: '20px 0', borderRadius: '8px' }}>
        <h2 style={{ color: '#2c5aa0' }}>Featured Story</h2>
        <p>{featuredStory}</p>
      </div>

      <p style={{ textAlign: 'center', color: '#666', fontSize: '14px' }}>
        Thank you for reading!
      </p>
    </div>
  );
}
