{"name":"simple-test","description":"Simple test layout without TypeScript interfaces","category":"newsletters","jsxCode":"import React from 'react';

export default function SimpleTestEmail({ firstName, lastName }) {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '600px', margin: '0 auto' }}>
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h1 style={{ color: '#333' }}>Hello {firstName} {lastName}!</h1>
        <p style={{ fontSize: '16px', color: '#666' }}>
          This is a simple test email template.
        </p>
        <p style={{ fontSize: '14px', color: '#999' }}>
          Thank you for testing our email system!
        </p>
      </div>
    </div>
  );
}"}
