{"name":"monthly-update","description":"Monthly newsletter update layout","category":"newsletters","jsxCode":"import React from 'react';

interface MonthlyUpdateProps {
  firstName: string;
  lastName: string;
  month: string;
}

const MonthlyUpdate: React.FC<MonthlyUpdateProps> = ({ firstName, lastName, month }) => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '600px', margin: '0 auto' }}>
      <h1 style={{ color: '#333', textAlign: 'center' }}>Monthly Update - {month}</h1>
      <p>Dear {firstName} {lastName},</p>
      <p>Here's what happened this month in our community!</p>
      <p>Best regards,<br/>The Newsletter Team</p>
    </div>
  );
};

export default MonthlyUpdate;"}
